REPLACE INTO `modx_site_htmlsnippets` VALUES (2,1,0,'tpl.AjaxForm.example','',0,8,0,'<form action=\"\" method=\"post\" class=\"ajax_form af_example form-horizontal\">\n\n	<div class=\"control-group\">\n		<label class=\"control-label\" for=\"af_name\">[[%af_label_name]]</label>\n		<div class=\"controls\">\n			<input type=\"text\" id=\"af_name\" name=\"name\" value=\"[[+fi.name]]\" placeholder=\"\" class=\"span4\" />\n			<span class=\"error_name\">[[+fi.error.name]]</span>\n		</div>\n	</div>\n\n	<div class=\"control-group\">\n		<label class=\"control-label\" for=\"af_email\">[[%af_label_email]]</label>\n		<div class=\"controls\">\n			<input type=\"email\" id=\"af_email\" name=\"email\" value=\"[[+fi.email]]\" placeholder=\"\" class=\"span4\" />\n			<span class=\"error_email\">[[+fi.error.email]]</span>\n		</div>\n	</div>\n\n	<div class=\"control-group\">\n		<label class=\"control-label\" for=\"af_message\">[[%af_label_message]]</label>\n		<div class=\"controls\">\n			<textarea id=\"af_message\" name=\"message\" class=\"span4\" rows=\"5\">[[+fi.message]]</textarea>\n			<span class=\"error_message\">[[+fi.error.message]]</span>\n		</div>\n	</div>\n\n	<div class=\"control-group\">\n		<div class=\"controls\">\n			<button type=\"reset\" class=\"btn btn-default\">[[%af_reset]]</button>\n			<button type=\"submit\" class=\"btn btn-primary\">[[%af_submit]]</button>\n		</div>\n	</div>\n	\n	[[+fi.success:is=`1`:then=`\n		<div class=\"alert alert-success\">[[+fi.successMessage]]</div>\n	`]]\n	[[+fi.validation_error:is=`1`:then=`\n		<div class=\"alert alert-danger\">[[+fi.validation_error_message]]</div>\n	`]]\n</form>',0,NULL,0,'core/components/ajaxform/elements/chunks/chunk.example.tpl');
REPLACE INTO `modx_site_htmlsnippets` VALUES (45,0,0,'fiDefaultEmailTpl','The default chunk used for the email. Please do not edit this chunk, as this will be overwritten when updating FormIt.',0,1,0,'<p>[[+fields]]</p>',0,'a:0:{}',0,'');
REPLACE INTO `modx_site_htmlsnippets` VALUES (46,0,0,'fiDefaultFiarTpl','The default chunk used for the autoresponder email. Please do not edit this chunk, as this will be overwritten when updating FormIt.',0,1,0,'<p>Hello [[+name]],</p>\n\n<p>Your message has been received. We will respond as soon as possible. Thank you for contacting us.</p>\n\n<p>NOTE: This is an automatic response; please do not respond to this message directly.</p>\n\n<p>Here is your message:<br />\n[[+message:nl2br]]</p>',0,'a:0:{}',0,'');
REPLACE INTO `modx_site_htmlsnippets` VALUES (47,0,0,'fiDefaultOptGroupTpl','The default chunk used by the FormItCountryOptions snippet for the select optgroup. Please do not edit this chunk, as this will be overwritten when updating FormIt.',0,1,0,'<optgroup label=\"[[+text]]\">\n    [[+options]]\n</optgroup>',0,'a:0:{}',0,'');
REPLACE INTO `modx_site_htmlsnippets` VALUES (48,0,0,'fiDefaultOptionTpl','The default chunk used by the FormItCountryOptions snippet for the select option. Please do not edit this chunk, as this will be overwritten when updating FormIt.',0,1,0,'<option value=\"[[+value]]\"[[+selected]]>[[+text]]</option>',0,'a:0:{}',0,'');
REPLACE INTO `modx_site_htmlsnippets` VALUES (70,1,0,'msProduct.content','',0,18,0,'<h1 class=\"text-center text-md-left\">[[*pagetitle]]</h1>\r\n<div class=\"text-center text-md-left mb-2 mb-md-0\">\r\n    [[+new:isnot=``:then=`<span class=\"badge badge-secondary badge-pill col-auto\">[[%ms2_frontend_new]]</span>`]]\r\n    [[+popular:isnot=``:then=`<span class=\"badge badge-secondary badge-pill col-auto\">[[%ms2_frontend_popular]]</span>`]]\r\n    [[+favorite:isnot=``:then=`<span class=\"badge badge-secondary badge-pill col-auto\">[[%ms2_frontend_favorite]]</span>`]]\r\n</div>\r\n<div id=\"msProduct\" class=\"row align-items-center\">\r\n    <div class=\"col-12 col-md-6\">\r\n        [[!msGallery]]\r\n    </div>\r\n    <div class=\"col-12 col-md-6\">\r\n        <form class=\"form-horizontal ms2_form\" method=\"post\">\r\n            <input type=\"hidden\" name=\"id\" value=\"[[*id]]\"/>\r\n\r\n\r\n\r\n            <div class=\"form-group row align-items-center\">\r\n                <label class=\"col-6 col-md-3 text-right text-md-left col-form-label\">[[%ms2_product_article]]:</label>\r\n                <div class=\"col-6 col-md-9\">\r\n                    [[+article:default=`-`]]\r\n                </div>\r\n            </div>\r\n            <div class=\"form-group row align-items-center\">\r\n                <label class=\"col-6 col-md-3 text-right text-md-left col-form-label\">[[%ms2_product_price]]:</label>\r\n                <div class=\"col-6 col-md-9\">\r\n                    [[+price]] [[%ms2_frontend_currency]]\r\n                    [[+old_price:gt=`0`:then=`\r\n                    <span class=\"old_price ml-2\">[[+old_price]] [[%ms2_frontend_currency]]</span>\r\n                    `:else=``]]\r\n                </div>\r\n            </div>\r\n            <div class=\"form-group row align-items-center\">\r\n                <label class=\"col-6 col-md-3 text-right text-md-left col-form-label\" for=\"product_price\">[[%ms2_cart_count]]:</label>\r\n                <div class=\"col-6 col-md-9\">\r\n                    <div class=\"input-group\">\r\n                        <input type=\"number\" name=\"count\" id=\"product_price\" class=\"form-control col-md-6\" value=\"1\"/>\r\n                        <div class=\"input-group-append\">\r\n                            <span class=\"input-group-text\">[[%ms2_frontend_count_unit]]</span>\r\n                        </div>\r\n                    </div>\r\n                </div>\r\n            </div>\r\n            <div class=\"form-group row align-items-center\">\r\n                <label class=\"col-6 col-md-3 text-right text-md-left col-form-label\">[[%ms2_product_weight]]:</label>\r\n                <div class=\"col-6 col-md-9\">\r\n                    [[+weight]] [[%ms2_frontend_weight_unit]]\r\n                </div>\r\n            </div>\r\n\r\n            <div class=\"form-group row align-items-center\">\r\n                <label class=\"col-6 col-md-3 text-right text-md-left col-form-label\">[[%ms2_product_made_in]]:</label>\r\n                <div class=\"col-6 col-md-9\">\r\n                    [[+made_in:default=`-`]]\r\n                </div>\r\n            </div>\r\n\r\n            [[msOptions?options=`color,size`]]\r\n\r\n            [[msProductOptions]]\r\n\r\n            <div class=\"form-group row align-items-center\">\r\n                <div class=\"col-12 offset-md-3 col-md-9 text-center text-md-left\">\r\n                    <button type=\"submit\" class=\"btn btn-primary\" name=\"ms2_action\" value=\"cart/add\">\r\n                        [[%ms2_frontend_add_to_cart]]\r\n                    </button>\r\n                </div>\r\n            </div>\r\n        </form>\r\n\r\n    </div>\r\n</div>\r\n<div class=\"mt-3\">\r\n    [[*content]]\r\n</div>',0,NULL,0,'core/components/minishop2/elements/chunks/chunk.ms_product_content.tpl');
REPLACE INTO `modx_site_htmlsnippets` VALUES (71,1,0,'tpl.msProducts.row','',0,18,0,'<div class=\"ms2_product mb-5 mb-md-3\">\r\n    <form method=\"post\" class=\"ms2_form d-flex flex-column flex-md-row align-items-center no-gutters\">\r\n        <input type=\"hidden\" name=\"id\" value=\"{$id}\">\r\n        <input type=\"hidden\" name=\"count\" value=\"1\">\r\n        <input type=\"hidden\" name=\"options\" value=\"[]\">\r\n        <div class=\"col-md-2 text-center text-md-left\">\r\n            <a href=\"{$id | url}\">\r\n                {if $thumb?}\r\n                    <img src=\"{$thumb}\" class=\"mw-100\" alt=\"{$pagetitle}\" title=\"{$pagetitle}\"/>\r\n                {else}\r\n                    <img src=\"{\'assets_url\' | option}components/minishop2/img/web/ms2_small.png\"\r\n                         srcset=\"{\'assets_url\' | option}components/minishop2/img/web/ms2_small@2x.png 2x\"\r\n                         class=\"mw-100\" alt=\"{$pagetitle}\" title=\"{$pagetitle}\"/>\r\n                {/if}\r\n            </a>\r\n        </div>\r\n        <div class=\"col-md-10 d-flex flex-column flex-md-row align-items-center no-gutters\">\r\n            <div class=\"col-12 col-md-8 mt-2 mt-md-0 flex-grow-1\">\r\n                <div class=\"d-flex justify-content-around justify-content-md-start\">\r\n                    <a href=\"{$id | url}\" class=\"font-weight-bold\">{$pagetitle}</a>\r\n                    <span class=\"price ml-md-3\">{$price} {\'ms2_frontend_currency\' | lexicon}</span>\r\n                    {if $old_price?}\r\n                        <span class=\"old_price ml-md-3\">{$old_price} {\'ms2_frontend_currency\' | lexicon}</span>\r\n                    {/if}\r\n                </div>\r\n                <div class=\"flags mt-2 d-flex justify-content-around justify-content-md-start\">\r\n                    {if $new?}\r\n                        <span class=\"badge badge-secondary badge-pill mr-md-1\">{\'ms2_frontend_new\' | lexicon}</span>\r\n                    {/if}\r\n                    {if $popular?}\r\n                        <span class=\"badge badge-secondary badge-pill mr-md-1\">{\'ms2_frontend_popular\' | lexicon}</span>\r\n                    {/if}\r\n                    {if $favorite?}\r\n                        <span class=\"badge badge-secondary badge-pill mr-md-1\">{\'ms2_frontend_favorite\' | lexicon}</span>\r\n                    {/if}\r\n                </div>\r\n                {if $introtext}\r\n                    <div class=\"mt-2 text-center text-md-left\">\r\n                        <small>{$introtext | truncate : 200}</small>\r\n                    </div>\r\n                {/if}\r\n            </div>\r\n            <div class=\"col-12 col-md-4 mt-2 mt-md-0 text-center text-md-right\">\r\n                <button class=\"btn btn-primary\" type=\"submit\" name=\"ms2_action\" value=\"cart/add\">\r\n                    {\'ms2_frontend_add_to_cart\' | lexicon}\r\n                </button>\r\n            </div>\r\n        </div>\r\n    </form>\r\n</div>',0,NULL,0,'core/components/minishop2/elements/chunks/chunk.ms_products_row.tpl');
REPLACE INTO `modx_site_htmlsnippets` VALUES (72,1,0,'tpl.msCart','',0,18,0,'<div id=\"msCart\">\r\n    {if !count($products)}\r\n        <div class=\"alert alert-warning\">\r\n            {\'ms2_cart_is_empty\' | lexicon}\r\n        </div>\r\n    {else}\r\n        <div class=\"table-responsive\">\r\n            <table class=\"table table-striped\">\r\n                <tr class=\"header\">\r\n                    <th class=\"title\">{\'ms2_cart_title\' | lexicon}</th>\r\n                    <th class=\"count\">{\'ms2_cart_count\' | lexicon}</th>\r\n                    <th class=\"weight\">{\'ms2_cart_weight\' | lexicon}</th>\r\n                    <th class=\"price\">{\'ms2_cart_price\' | lexicon}</th>\r\n                    <th class=\"remove\"></th>\r\n                </tr>\r\n\r\n                {foreach $products as $product}\r\n                    {var $image}\r\n                        {if $product.thumb?}\r\n                            <img src=\"{$product.thumb}\" alt=\"{$product.pagetitle}\" title=\"{$product.pagetitle}\"/>\r\n                        {else}\r\n                            <img src=\"{\'assets_url\' | option}components/minishop2/img/web/ms2_small.png\"\r\n                                 srcset=\"{\'assets_url\' | option}components/minishop2/img/web/ms2_small@2x.png 2x\"\r\n                                 alt=\"{$product.pagetitle}\" title=\"{$product.pagetitle}\"/>\r\n                        {/if}\r\n                    {/var}\r\n                    <tr id=\"{$product.key}\">\r\n                        <td class=\"title\">\r\n                            <div class=\"d-flex\">\r\n                                <div class=\"image mw-100 pr-3\">\r\n                                    {if $product.id?}\r\n                                        <a href=\"{$product.id | url}\">{$image}</a>\r\n                                    {else}\r\n                                        {$image}\r\n                                    {/if}\r\n                                </div>\r\n                                <div class=\"title\">\r\n                                    {if $product.id?}\r\n                                        <a href=\"{$product.id | url}\">{$product.pagetitle}</a>\r\n                                    {else}\r\n                                        {$product.name}\r\n                                    {/if}\r\n                                    {if $product.options?}\r\n                                        <div class=\"small\">\r\n                                            {$product.options | join : \'; \'}\r\n                                        </div>\r\n                                    {/if}\r\n                                </div>\r\n                            </div>\r\n                        </td>\r\n                        <td class=\"count\">\r\n                            <form method=\"post\" class=\"ms2_form\" role=\"form\">\r\n                                <input type=\"hidden\" name=\"key\" value=\"{$product.key}\"/>\r\n                                <div class=\"form-group\">\r\n                                    <div class=\"input-group input-group-sm\">\r\n                                        <input type=\"number\" name=\"count\" value=\"{$product.count}\" class=\"form-control\"/>\r\n                                        <div class=\"input-group-append\">\r\n                                            <span class=\"input-group-text\">{\'ms2_frontend_count_unit\' | lexicon}</span>\r\n                                        </div>\r\n                                    </div>\r\n                                    <button class=\"btn btn-sm\" type=\"submit\" name=\"ms2_action\" value=\"cart/change\">&#8635;</button>\r\n                                </div>\r\n                            </form>\r\n                        </td>\r\n                        <td class=\"weight\">\r\n                            <span class=\"text-nowrap\">{$product.weight} {\'ms2_frontend_weight_unit\' | lexicon}</span>\r\n                        </td>\r\n                        <td class=\"price\">\r\n                            <span class=\"mr-2 text-nowrap\">{$product.price} {\'ms2_frontend_currency\' | lexicon}</span>\r\n                            {if $product.old_price?}\r\n                                <span class=\"old_price text-nowrap\">{$product.old_price} {\'ms2_frontend_currency\' | lexicon}</span>\r\n                            {/if}\r\n                        </td>\r\n                        <td class=\"remove\">\r\n                            <form method=\"post\" class=\"ms2_form text-md-right\">\r\n                                <input type=\"hidden\" name=\"key\" value=\"{$product.key}\">\r\n                                <button class=\"btn btn-sm btn-danger\" type=\"submit\" name=\"ms2_action\" value=\"cart/remove\">&times;</button>\r\n                            </form>\r\n                        </td>\r\n                    </tr>\r\n                {/foreach}\r\n\r\n                <tr class=\"footer\">\r\n                    <th class=\"total\">{\'ms2_cart_total\' | lexicon}:</th>\r\n                    <th class=\"total_count\">\r\n                        <span class=\"ms2_total_count\">{$total.count}</span>\r\n                        {\'ms2_frontend_count_unit\' | lexicon}\r\n                    </th>\r\n                    <th class=\"total_weight text-nowrap\">\r\n                        <span class=\"ms2_total_weight\">{$total.weight}</span>\r\n                        {\'ms2_frontend_weight_unit\' | lexicon}\r\n                    </th>\r\n                    <th class=\"total_cost text-nowrap\" colspan=\"2\">\r\n                        <span class=\"ms2_total_cost\">{$total.cost}</span>\r\n                        {\'ms2_frontend_currency\' | lexicon}\r\n                    </th>\r\n                </tr>\r\n            </table>\r\n        </div>\r\n\r\n        <form method=\"post\">\r\n            <button type=\"submit\" name=\"ms2_action\" value=\"cart/clean\" class=\"btn btn-danger\">\r\n                {\'ms2_cart_clean\' | lexicon}\r\n            </button>\r\n        </form>\r\n    {/if}\r\n</div>\r\n',0,NULL,0,'core/components/minishop2/elements/chunks/chunk.ms_cart.tpl');
REPLACE INTO `modx_site_htmlsnippets` VALUES (73,1,0,'tpl.msMiniCart','',0,18,0,'<div id=\"msMiniCart\" class=\"{$total_count > 0 ? \'full\' : \'\'}\">\r\n    <div class=\"empty\">\r\n        <h5>{\'ms2_minicart\' | lexicon}</h5>\r\n        {\'ms2_minicart_is_empty\' | lexicon}\r\n    </div>\r\n    <div class=\"not_empty\">\r\n        <h5>{\'ms2_minicart\' | lexicon}</h5>\r\n        {\'ms2_minicart_goods\' | lexicon} <strong class=\"ms2_total_count\">{$total_count}</strong> {\'ms2_frontend_count_unit\' | lexicon},\r\n        {\'ms2_minicart_cost\' | lexicon} <strong class=\"ms2_total_cost\">{$total_cost}</strong> {\'ms2_frontend_currency\' | lexicon}\r\n    </div>\r\n</div>',0,NULL,0,'core/components/minishop2/elements/chunks/chunk.ms_minicart.tpl');
REPLACE INTO `modx_site_htmlsnippets` VALUES (74,1,0,'tpl.msOrder','',0,18,0,'<form class=\"ms2_form\" id=\"msOrder\" method=\"post\">\r\n    <div class=\"row\">\r\n        <div class=\"col-12 col-md-6\">\r\n            <h4>{\'ms2_frontend_credentials\' | lexicon}:</h4>\r\n            {foreach [\'email\',\'receiver\',\'phone\'] as $field}\r\n                <div class=\"form-group row input-parent\">\r\n                    <label class=\"col-md-4 col-form-label\" for=\"{$field}\">\r\n                        {(\'ms2_frontend_\' ~ $field) | lexicon} <span class=\"required-star\">*</span>\r\n                    </label>\r\n                    <div class=\"col-md-8\">\r\n                        <input type=\"text\" id=\"{$field}\" placeholder=\"{(\'ms2_frontend_\' ~ $field) | lexicon}\"\r\n                               name=\"{$field}\" value=\"{$form[$field]}\"\r\n                               class=\"form-control{($field in list $errors) ? \' error\' : \'\'}\">\r\n                    </div>\r\n                </div>\r\n            {/foreach}\r\n\r\n            <div class=\"form-group row input-parent\">\r\n                <label class=\"col-md-4 col-form-label\" for=\"comment\">\r\n                    {\'ms2_frontend_comment\' | lexicon} <span class=\"required-star\">*</span>\r\n                </label>\r\n                <div class=\"col-md-8\">\r\n                    <textarea name=\"comment\" id=\"comment\" placeholder=\"{\'ms2_frontend_comment\' | lexicon}\"\r\n                              class=\"form-control{(\'comment\' in list $errors) ? \' error\' : \'\'}\">{$form.comment}</textarea>\r\n                </div>\r\n            </div>\r\n        </div>\r\n\r\n        <div class=\"col-12 col-md-6\" id=\"payments\">\r\n            <h4>{\'ms2_frontend_payments\' | lexicon}:</h4>\r\n            <div class=\"form-group row\">\r\n                <div class=\"col-12\">\r\n                    {foreach $payments as $payment index=$index}\r\n                        {var $checked = !($order.payment in keys $payments) && $index == 0 || $payment.id == $order.payment}\r\n                        <div class=\"checkbox\">\r\n                            <label class=\"col-form-label payment input-parent\">\r\n                                <input type=\"radio\" name=\"payment\" value=\"{$payment.id}\" id=\"payment_{$payment.id}\"{$checked ? \'checked\' : \'\'}>\r\n                                {if $payment.logo?}\r\n                                    <img src=\"{$payment.logo}\" alt=\"{$payment.name}\" title=\"{$payment.name}\" class=\"mw-100\"/>\r\n                                {else}\r\n                                    {$payment.name}\r\n                                {/if}\r\n                                {if $payment.description?}\r\n                                    <p class=\"small\">{$payment.description}</p>\r\n                                {/if}\r\n                            </label>\r\n                        </div>\r\n                    {/foreach}\r\n                </div>\r\n            </div>\r\n        </div>\r\n    </div>\r\n\r\n    <div class=\"row\">\r\n        <div class=\"col-12 col-md-6\" id=\"deliveries\">\r\n            <h4>{\'ms2_frontend_deliveries\' | lexicon}:</h4>\r\n            <div class=\"form-group row\">\r\n                <div class=\"col-12\">\r\n                    {foreach $deliveries as $delivery index=$index}\r\n                        {var $checked = !($order.delivery in keys $deliveries) && $index == 0 || $delivery.id == $order.delivery}\r\n                        <div class=\"checkbox\">\r\n                            <label class=\"col-form-label delivery input-parent\">\r\n                                <input type=\"radio\" name=\"delivery\" value=\"{$delivery.id}\" id=\"delivery_{$delivery.id}\"\r\n                                       data-payments=\"{$delivery.payments | json_encode}\"\r\n                                        {$checked ? \'checked\' : \'\'}>\r\n                                {if $delivery.logo?}\r\n                                    <img src=\"{$delivery.logo}\" alt=\"{$delivery.name}\" title=\"{$delivery.name}\"/>\r\n                                {else}\r\n                                    {$delivery.name}\r\n                                {/if}\r\n                                {if $delivery.description?}\r\n                                    <p class=\"small\">\r\n                                        {$delivery.description}\r\n                                    </p>\r\n                                {/if}\r\n                            </label>\r\n                        </div>\r\n                    {/foreach}\r\n                </div>\r\n            </div>\r\n        </div>\r\n\r\n        <div class=\"col-12 col-md-6\">\r\n            <h4>{\'ms2_frontend_address\' | lexicon}:</h4>\r\n            {foreach [\'index\',\'region\',\'city\'] as $field}\r\n                <div class=\"form-group row input-parent\">\r\n                    <label class=\"col-md-4 col-form-label\" for=\"{$field}\">\r\n                        {(\'ms2_frontend_\' ~ $field) | lexicon} <span class=\"required-star\">*</span>\r\n                    </label>\r\n                    <div class=\"col-md-8\">\r\n                        <input type=\"text\" id=\"{$field}\" placeholder=\"{(\'ms2_frontend_\' ~ $field) | lexicon}\"\r\n                               name=\"{$field}\" value=\"{$form[$field]}\"\r\n                               class=\"form-control{($field in list $errors) ? \' error\' : \'\'}\">\r\n                    </div>\r\n                </div>\r\n            {/foreach}\r\n            <div class=\"form-group row input-parent\">\r\n                <label class=\"col-md-4 col-form-label\" for=\"street\">\r\n                    {\'ms2_frontend_street\' | lexicon}</label> <span class=\"required-star\">*</span>\r\n                <div class=\"col-md-8 row no-gutters\">\r\n                    {foreach [\'street\' => 6, \'building\' => 3, \'room\' => 3] as $field => $col}\r\n                        <div class=\"col-{$col}\">\r\n                            <input type=\"text\" id=\"{$field}\" placeholder=\"{(\'ms2_frontend_\' ~ $field) | lexicon}\"\r\n                                   name=\"{$field}\" value=\"{$form[$field]}\"\r\n                                   class=\"form-control{($field in list $errors) ? \' error\' : \'\'}\">\r\n                        </div>\r\n                    {/foreach}\r\n                </div>\r\n            </div>\r\n        </div>\r\n\r\n    </div>\r\n\r\n    <button type=\"button\" name=\"ms2_action\" value=\"order/clean\" class=\"btn btn-danger ms2_link\">\r\n        {\'ms2_frontend_order_cancel\' | lexicon}\r\n    </button>\r\n\r\n    <hr class=\"mt-4 mb-4\"/>\r\n\r\n    <div class=\"d-flex flex-column flex-md-row align-items-center justify-content-center justify-content-md-end\">\r\n        <h4 class=\"mb-md-0\">{\'ms2_frontend_order_cost\' | lexicon}:</h4>\r\n        <h3 class=\"mb-md-0 ml-md-2\"><span id=\"ms2_order_cost\">{$order.cost ?: 0}</span> {\'ms2_frontend_currency\' | lexicon}</h3>\r\n\r\n        <button type=\"submit\" name=\"ms2_action\" value=\"order/submit\" class=\"btn btn-lg btn-primary ml-md-2 ms2_link\">\r\n            {\'ms2_frontend_order_submit\' | lexicon}\r\n        </button>\r\n    </div>\r\n</form>',0,NULL,0,'core/components/minishop2/elements/chunks/chunk.ms_order.tpl');
