r FITNESS FOR\n * A PARTICULAR PURPOSE. See the GNU General Public License for more details.\n *\n * You should have received a copy of the GNU General Public License along with\n * FormIt; if not, write to the Free Software Foundation, Inc., 59 Temple Place,\n * Suite 330, Boston, MA 02111-1307 USA\n *\n * @package formit\n */\n/**\n * A custom FormIt prehook for fetching saved form data. - Based on FormItSaveForm\n *\n * @var modX $modx\n * @var array $scriptProperties\n * @var FormIt $formit\n * @var fiHooks $hook\n * \n * @package formit\n */\n/* setup default properties */\n// If prehook fails do we continue?\n$return = $modx->getOption(\'returnValueOnFail\', $formit->config, true);\n$formEncrypt = $modx->getOption(\'formEncrypt\', $formit->config, false);\n$formFields = $modx->getOption(\'formFields\', $formit->config, false);\n$fieldNames = $modx->getOption(\'fieldNames\', $formit->config, false);\n$updateSavedForm = $modx->getOption(\'updateSavedForm\', $formit->config, false); // true, false, \'1\', \'0\', or \'values\'\n// If FormIt config says don\'t update, don\'t do it\nif (!$updateSavedForm) return $return;\n// In order to load form values, the user must provide the hash key somehow\n// Usually with a $_GET parameter, but a property in $formit->config will override.\n$formHashKeyField = $modx->getOption(\'savedFormHashKeyField\', $formit->config, \'savedFormHashKey\');\n$formHashKey = \'\';\nif (isset($_GET[$formHashKeyField])) $formHashKey = (string) $_GET[$formHashKeyField];\nif ($hook->getValue($formHashKeyField)) $formHashKey = (string) $hook->getValue($formHashKeyField);\nif (isset($formit->config[$formHashKeyField])) $formHashKey = $formit->config[$formHashKeyField];\n// our hashing methods return 32 chars. if no valid hash key we\'re done here.\nif (strlen($formHashKey) !== 32) return $return;\n\n// Try to fetch the saved form\n$savedForm = $modx->getObject(\'FormItForm\', array(\'hash\' => $formHashKey));\nif (!$savedForm) return $return;\n\nif ($formFields) {\n    $formFields = explode(\',\', $formFields);\n    foreach($formFields as $k => $v) {\n        $formFields[$k] = trim($v);\n    }\n}\n\n// Initialize the data array\n// Handle encryption\nif ($formEncrypt) {\n    $encrypted_data = $savedForm->get(\'values\');\n    $data = $savedForm->decrypt($encrypted_data);\n} else {\n    $data = $savedForm->get(\'values\');\n}\nif (is_string($data)) $data = $modx->fromJSON($data);\nif (!is_array($data)) return $return;\n\n//Change the fieldnames\nif ($fieldNames) {\n    $newDataArray = array();\n    $fieldLabels = array();\n    $formFieldNames = explode(\',\', $fieldNames);\n    foreach($formFieldNames as $formFieldName){\n        list($name, $label) = explode(\'==\', $formFieldName);\n        // reverse order from FormItSaveForm snippet\n        $fieldLabels[trim($label)] = trim($name);\n    }\n    foreach ($data as $key => $value) {\n        if ($fieldLabels[$key]) {\n            $newDataArray[$fieldLabels[$key]] = $value;\n        }else{\n            $newDataArray[$key] = $value;\n        }\n    }\n    $data = $newDataArray;\n}\n\n// Always pass back in the provided hash key\n$data[$formHashKeyField] = $formHashKey;\n$hook->setValues($data);\nreturn true;',0,'a:0:{}','',0,''),(66,1,0,'msProducts','',0,18,0,'/** @var modX $modx */\r\n/** @var array $scriptProperties */\r\n/** @var miniShop2 $miniShop2 */\r\n$miniShop2 = $modx->getService(\'miniShop2\');\r\n$miniShop2->initialize($modx->context->key);\r\n/** @var pdoFetch $pdoFetch */\r\n$fqn = $modx->getOption(\'pdoFetch.class\', null, \'pdotools.pdofetch\', true);\r\n$path = $modx->getOption(\'pdofetch_class_path\', null, MODX_CORE_PATH . \'components/pdotools/model/\', true);\r\nif ($pdoClass = $modx->loadClass($fqn, $path, false, true)) {\r\n    $pdoFetch = new $pdoClass($modx, $scriptProperties);\r\n} else {\r\n    return false;\r\n}\r\n$pdoFetch->addTime(\'pdoTools loaded.\');\r\n\r\nif (isset($parents) && $parents === \'\') {\r\n    $scriptProperties[\'parents\'] = $modx->resource->id;\r\n}\r\n\r\n// Start build \"where\" expression\r\n$where = array(\r\n    \'class_key\' => \'msProduct\',\r\n);\r\nif (empty($showZeroPrice)) {\r\n    $where[\'Data.price:>\'] = 0;\r\n}\r\n// Add grouping\r\n$groupby = array(\r\n    \'msProduct.id\',\r\n);\r\n\r\n// Join tables\r\n$leftJoin = array(\r\n    \'Data\' => array(\'class\' => \'msProductData\'),\r\n    \'Vendor\' => array(\'class\' => \'msVendor\', \'on\' => \'Data.vendor=Vendor.id\'),\r\n);\r\n\r\n$select = array(\r\n    \'msProduct\' => !empty($includeContent)\r\n        ? $modx->getSelectColumns(\'msProduct\', \'msProduct\')\r\n        : $modx->getSelectColumns(\'msProduct\', \'msProduct\', \'\', array(\'content\'), true),\r\n    \'Data\' => $modx->getSelectColumns(\'msProductData\', \'Data\', \'\', array(\'id\'), true),\r\n    \'Vendor\' => $modx->getSelectColumns(\'msVendor\', \'Vendor\', \'vendor.\', array(\'id\'), true),\r\n);\r\n\r\n// Include thumbnails\r\nif (!empty($includeThumbs)) {\r\n    $thumbs = array_map(\'trim\', explode(\',\', $includeThumbs));\r\n    foreach ($thumbs as $thumb) {\r\n        if (empty($thumb)) {\r\n            continue;\r\n        }\r\n        $leftJoin[$thumb] = array(\r\n            \'class\' => \'msProductFile\',\r\n            \'on\' => \"`{$thumb}`.product_id = msProduct.id AND `{$thumb}`.rank = 0 AND `{$thumb}`.path LIKE \'%/{$thumb}/%\'\",\r\n        );\r\n        $select[$thumb] = \"`{$thumb}`.url as `{$thumb}`\";\r\n        $groupby[] = \"`{$thumb}`.url\";\r\n    }\r\n}\r\n\r\n// Include linked products\r\n$innerJoin = array();\r\nif (!empty($link) && !empty($master)) {\r\n    $innerJoin[\'Link\'] = array(\r\n        \'class\' => \'msProductLink\',\r\n        \'on\' => \'msProduct.id = Link.slave AND Link.link = \' . $link,\r\n    );\r\n    $where[\'Link.master\'] = $master;\r\n} elseif (!empty($link) && !empty($slave)) {\r\n    $innerJoin[\'Link\'] = array(\r\n        \'class\' => \'msProductLink\',\r\n        \'on\' => \'msProduct.id = Link.master AND Link.link = \' . $link,\r\n    );\r\n    $where[\'Link.slave\'] = $slave;\r\n}\r\n\r\n// Add user parameters\r\nforeach (array(\'where\', \'leftJoin\', \'innerJoin\', \'select\', \'groupby\') as $v) {\r\n    if (!empty($scriptProperties[$v])) {\r\n        $tmp = $scriptProperties[$v];\r\n        if (!is_array($tmp)) {\r\n            $tmp = json_decode($tmp, true);\r\n        }\r\n        if (is_array($tmp)) {\r\n            $$v = array_merge($$v, $tmp);\r\n        }\r\n    }\r\n    unset($scriptProperties[$v]);\r\n}\r\n$pdoFetch->addTime(\'Conditions prepared\');\r\n\r\n// Add filters by options\r\n$joinedOptions = array();\r\nif (!empty($scriptProperties[\'optionFilters\'])) {\r\n    $filters = json_decode($scriptProperties[\'optionFilters\'], true);\r\n    foreach ($filters as $key => $value) {\r\n        $option = preg_replace(\'#\\:.*#\', \'\', $key);\r\n        $key = str_replace($option, $option . \'.value\', $key);\r\n        if (!in_array($option, $joinedOptions)) {\r\n            $leftJoin[$option] = array(\r\n                \'class\' => \'msProductOption\',\r\n                \'on\' => \"`{$option}`.product_id = Data.id AND `{$option}`.key = \'{$option}\'\",\r\n            );\r\n            $joinedOptions[] = $option;\r\n            $where[$key] = $value;\r\n        }\r\n    }\r\n}\r\n\r\n// Add sort by options\r\nif (!empty($scriptProperties[\'sortbyOptions\'])) {\r\n    $sorts = array_map(\'trim\', explode(\',\', $scriptProperties[\'sortbyOptions\']));\r\n    foreach ($sorts as $sort) {\r\n        $sort = explode(\':\', $sort);\r\n        $option = $sort[0];\r\n        if (preg_match(\"#\\b{$option}\\b#\", $scriptProperties[\'sortby\'], $matches)) {\r\n            $type = \'string\';\r\n            if (isset($sort[1])) {\r\n                $type = $sort[1];\r\n            }\r\n            switch ($type) {\r\n                case \'number\':\r\n                case \'decimal\':\r\n                    $sortbyOptions = \"CAST(`{$option}`.`value` AS DECIMAL(13,3))\";\r\n                    break;\r\n                case \'int\':\r\n                case \'integer\':\r\n                    $sortbyOptions = \"CAST(`{$option}`.`value` AS UNSIGNED INTEGER)\";\r\n                    break;\r\n                case \'date\':\r\n                case \'datetime\':\r\n                    $sortbyOptions = \"CAST(`{$option}`.`value` AS DATETIME)\";\r\n                    break;\r\n                default:\r\n                    $sortbyOptions = \"`{$option}`.`value`\";\r\n                    break;\r\n            }\r\n            $scriptProperties[\'sortby\'] = preg_replace(\"#\\b{$option}\\b#\", $sortbyOptions, $scriptProperties[\'sortby\']);\r\n            $groupby[] = \"`{$option}`.value\";\r\n        }\r\n\r\n        if (!in_array($option, $joinedOptions)) {\r\n            $leftJoin[$option] = array(\r\n                \'class\' => \'msProductOption\',\r\n                \'on\' => \"`{$option}`.product_id = Data.id AND `{$option}`.key = \'{$option}\'\",\r\n            );\r\n            $joinedOptions[] = $option;\r\n        }\r\n\r\n    }\r\n}\r\n\r\n$default = array(\r\n    \'class\' => \'msProduct\',\r\n    \'where\' => $where,\r\n    \'leftJoin\' => $leftJoin,\r\n    \'innerJoin\' => $innerJoin,\r\n    \'select\' => $select,\r\n    \'sortby\' => \'msProduct.id\',\r\n    \'sortdir\' => \'ASC\',\r\n    \'groupby\' => implode(\', \', $groupby),\r\n    \'return\' => !empty($returnIds)\r\n        ? \'ids\'\r\n        : \'data\',\r\n    \'nestedChunkPrefix\' => \'minishop2_\',\r\n);\r\n// Merge all properties and run!\r\n$pdoFetch->setConfig(array_merge($default, $scriptProperties), false);\r\n$rows = $pdoFetch->run();\r\n\r\n// Process rows\r\n$output = array();\r\nif (!empty($rows) && is_array($rows)) {\r\n    $c = $modx->newQuery(\'modPluginEvent\', array(\'event:IN\' => array(\'msOnGetProductPrice\', \'msOnGetProductWeight\')));\r\n    $c->innerJoin(\'modPlugin\', \'modPlugin\', \'modPlugin.id = mod